    private void btnPiedraActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPiedraActionPerformed
        if (aleatorio==1)
        {
            JOptionPane.showMessageDialog(null,"Piedra, empate");
        }
        if (aleatorio==2)
        {
            JOptionPane.showMessageDialog(null,"Papel! El papel envuelve la piedra, perdiste!");
        }
        if (aleatorio==3)
        {
            JOptionPane.showMessageDialog(null,"Tijeras! Piedra aplasta a tijeras, ganaste!");
        }
        if (aleatorio==4)
        {
            JOptionPane.showMessageDialog(null,"Lagarto! Piedra aplasta al lagarto, ganaste!");
        }
        if (aleatorio==5)
        {
            JOptionPane.showMessageDialog(null,"Spock! Spock vaporiza a la piedra, perdiste!");
        }
    }//GEN-LAST:event_btnPiedraActionPerformed

