    private void btnSpockActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSpockActionPerformed
         if (aleatorio==1)
        {
            JOptionPane.showMessageDialog(null,"Piedra! Spock vaporiza piedra, ganaste!");
        }
        if (aleatorio==2)
        {
            JOptionPane.showMessageDialog(null,"Papel! Papel desautoriza a Stock, perdiste!");
        }
        if (aleatorio==3)
        {
            JOptionPane.showMessageDialog(null,"Tijeras! Spock rompe tijeras, ganaste!");
        }
        if (aleatorio==4)
        {
            JOptionPane.showMessageDialog(null,"Lagarto! Lagarto envenena a spock, perdiste!");
        }
        if (aleatorio==5)
        {
            JOptionPane.showMessageDialog(null,"Spock! Empate");
        }
    }//GEN-LAST:event_btnSpockActionPerformed

